import React, { useState, useEffect } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [categoriasExtra, setCategoriasExtra] = useState([]);
  // Simula√ß√£o de quem est√° logado: 'ATLETA' ou 'ORGANIZADOR'
  const tipoLogado = "ATLETA"; 

  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasExtra(salvas);
  }, []);

  return (
    <div className="form-card">
      <h2>üéæ Cadastro de Atleta</h2>
      <p style={{ color: '#666' }}>Logado como: <strong>{tipoLogado}</strong></p>
      
      <form>
        <label style={{ fontSize: '20px', fontWeight: 'bold' }}>Nome do Atleta:</label>
        <input type="text" className="input-master" placeholder="Digite seu nome" style={{ width: '100%', padding: '15px', marginBottom: '20px' }} />

        <label style={{ fontSize: '20px', fontWeight: 'bold' }}>Selecione a Categoria:</label>
        <select style={{ width: '100%', padding: '15px', fontSize: '18px', borderRadius: '10px' }}>
          <option value="">-- Escolha uma Op√ß√£o --</option>
          
          {/* CATEGORIAS PADR√ÉO (Sem op√ß√£o de excluir/editar) */}
          <optgroup label="üèÜ CATEGORIAS OFICIAIS (N√≠vel T√©cnico)">
            <option>Masculino A (Pr√≥)</option><option>Masculino B</option>
            <option>Masculino C</option><option>Masculino D (Iniciante)</option>
            <option>Feminino A (Pr√≥)</option><option>Feminino B</option>
            <option>Feminino C</option><option>Feminino D (Iniciante)</option>
          </optgroup>

          <optgroup label="üîÑ MODALIDADES">
            <option>Dupla Mista A</option><option>Dupla Mista B</option>
            <option>Simples Masculino</option><option>Simples Feminino</option>
          </optgroup>

          <optgroup label="üéÇ FAIXA ET√ÅRIA">
            <option>Kids (Sub-12)</option><option>Juvenil (Sub-16)</option>
            <option>Master 40+</option><option>Master 50+</option>
          </optgroup>

          {/* CATEGORIAS PERSONALIZADAS (S√≥ aparecem se houver) */}
          {categoriasExtra.length > 0 && (
            <optgroup label="‚≠ê CATEGORIAS DA ARENA / ESPECIAIS">
              {categoriasExtra.map((c, i) => <option key={i} value={c}>{c}</option>)}
            </optgroup>
          )}
        </select>

        {/* CONTROLE DE ACESSO: Atleta n√£o v√™ bot√£o de criar nova categoria aqui */}
        {tipoLogado === "ORGANIZADOR" && (
           <p style={{ marginTop: '10px', color: 'blue', cursor: 'pointer' }}>+ Adicionar Categoria Extra</p>
        )}

        <div style={{ display: 'flex', gap: '20px', marginTop: '30px' }}>
          <button type="button" className="btn-sucesso" style={{ flex: 1 }}>CONFIRMAR</button>
          <button type="button" onClick={aoCancelar} style={{ flex: 1, backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '15px' }}>CANCELAR</button>
        </div>
      </form>
    </div>
  );
};

export default FormularioAtleta;
