import React, { useState, useEffect } from 'react';

const GerenciarCategorias = () => {
  const [categorias, setCategorias] = useState([]);
  const [nova, setNova] = useState('');
  const [editando, setEditando] = useState(null);

  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategorias(salvas);
  }, []);

  const atualizar = (lista) => {
    setCategorias(lista);
    localStorage.setItem('minhasCategorias', JSON.stringify(lista));
  };

  const salvarNova = (e) => {
    e.preventDefault();
    if (!nova.trim()) return;
    atualizar([...categorias, nova]);
    setNova('');
  };

  const deletar = (idx) => {
    if (window.confirm("Deseja apagar sua categoria personalizada?")) {
      atualizar(categorias.filter((_, i) => i !== idx));
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ color: 'var(--navy-dark)' }}>üìÇ Suas Categorias Personalizadas</h2>
      <p style={{ color: '#64748b', marginBottom: '30px' }}>Categorias criadas por voc√™ para este torneio.</p>

      <form onSubmit={salvarNova} style={{ display: 'flex', gap: '10px', marginBottom: '30px' }}>
        <input 
          type="text" value={nova} onChange={(e) => setNova(e.target.value)}
          placeholder="Ex: Categoria Pais e Filhos"
          style={{ flex: 1, padding: '15px', borderRadius: '10px', border: '1px solid #cbd5e1', fontSize: '18px' }}
        />
        <button type="submit" className="btn-sucesso">CRIAR NOVA</button>
      </form>

      <div>
        {categorias.map((cat, i) => (
          <div key={i} className="card-categoria">
            <span style={{ fontWeight: 'bold', fontSize: '18px' }}>{cat}</span>
            <div>
              <button onClick={() => {
                const novoNome = prompt("Novo nome da categoria:", cat);
                if (novoNome) {
                  const lista = [...categorias]; lista[i] = novoNome; atualizar(lista);
                }
              }} className="btn-icon btn-edit">‚úèÔ∏è Editar</button>
              <button onClick={() => deletar(i)} className="btn-icon btn-delete">üóëÔ∏è Excluir</button>
            </div>
          </div>
        ))}
      </div>

      <div style={{ marginTop: '50px', opacity: 0.6 }}>
        <span className="label-padrao">üîí Categorias Padr√£o do Sistema (Imut√°veis)</span>
        <div style={{ fontSize: '14px', marginTop: '10px' }}>
          Feminino/Masculino PRO a Iniciante, Subs, Masters, Open e Simples.
        </div>
      </div>
    </div>
  );
};

export default GerenciarCategorias;
