import React, { useState } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [nome, setNome] = useState('');
  const [categoria, setCategoria] = useState('Masculino A');
  const [novaCat, setNovaCat] = useState('');

  return (
    <div style={{ padding: '20px', border: '2px solid #1a2a6c', borderRadius: '15px', fontSize: '22px' }}>
      <h3 style={{ fontSize: '28px' }}>üìù Novo Cadastro</h3>
      <form onSubmit={(e) => { e.preventDefault(); aoSalvar(); }}>
        <div style={{ marginBottom: '20px' }}>
          <label>Nome Completo do Atleta:</label><br/>
          <input 
            type="text" 
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            style={{ width: '100%', padding: '15px', fontSize: '20px', borderRadius: '10px', border: '1px solid #ccc' }}
            required
          />
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Categoria:</label><br/>
          <select 
            value={categoria} 
            onChange={(e) => setCategoria(e.target.value)}
            style={{ width: '100%', padding: '15px', fontSize: '20px', borderRadius: '10px' }}
          >
            <optgroup label="Masculino">
              <option value="Masculino A">Masculino A (Pr√≥)</option>
              <option value="Masculino E">Masculino E (Iniciante)</option>
            </optgroup>
            <optgroup label="Feminino">
              <option value="Feminino A">Feminino A (Pr√≥)</option>
              <option value="Feminino E">Feminino E (Iniciante)</option>
            </optgroup>
            <option value="OUTRA">‚ûï CRIAR NOVA CATEGORIA...</option>
          </select>
        </div>

        <div style={{ display: 'flex', gap: '15px' }}>
          <button type="submit" style={{ flex: 1, padding: '20px', backgroundColor: '#27ae60', color: 'white', borderRadius: '10px', fontWeight: 'bold', fontSize: '20px', border: 'none' }}>SALVAR</button>
          <button type="button" onClick={aoCancelar} style={{ flex: 1, padding: '20px', backgroundColor: '#e74c3c', color: 'white', borderRadius: '10px', fontSize: '20px', border: 'none' }}>CANCELAR</button>
        </div>
      </form>
    </div>
  );
};

export default FormularioAtleta;
