import React, { useState } from 'react';
import './index.css';
import Atletas from './components/Atletas';
import FormularioAtleta from './components/FormularioAtleta';

function App() {
  const [mostrarForm, setMostrarForm] = useState(false);

  return (
    <div style={{ minHeight: '100vh' }}>
      {/* Barra de cima com letra bem grande */}
      <nav style={{ backgroundColor: '#1a2a6c', color: 'white', padding: '25px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', boxShadow: '0 4px 10px rgba(0,0,0,0.2)' }}>
        <h1 style={{ margin: 0, fontSize: '32px' }}>üèñÔ∏è Beach Master Pro</h1>
        <div style={{ fontSize: '18px', background: '#f39c12', padding: '5px 15px', borderRadius: '20px', fontWeight: 'bold' }}>MODO ADMIN</div>
      </nav>
      
      <div style={{ display: 'grid', gridTemplateColumns: '280px 1fr', gap: '30px', padding: '30px' }}>
        {/* Menu Lateral com bot√µes f√°ceis de clicar */}
        <aside style={{ backgroundColor: 'white', padding: '20px', borderRadius: '15px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', height: 'fit-content' }}>
          <ul style={{ listStyle: 'none', padding: 0, fontSize: '22px' }}>
            <li style={{ padding: '15px', cursor: 'pointer', borderBottom: '1px solid #eee', color: '#1a2a6c', fontWeight: 'bold' }}>üè† In√≠cio</li>
            <li style={{ padding: '15px', cursor: 'pointer', borderBottom: '1px solid #eee' }}>üë• Atletas</li>
            <li style={{ padding: '15px', cursor: 'pointer', borderBottom: '1px solid #eee' }}>üèÜ Torneios</li>
            <li style={{ padding: '15px', cursor: 'pointer', color: '#e74c3c' }}>üö™ Sair</li>
          </ul>
        </aside>

        {/* √Årea Principal */}
        <main style={{ backgroundColor: 'white', padding: '40px', borderRadius: '20px', boxShadow: '0 4px 20px rgba(0,0,0,0.08)' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
            <h2 style={{ fontSize: '35px', margin: 0 }}>Gest√£o de Atletas</h2>
            {!mostrarForm && (
              <button 
                onClick={() => setMostrarForm(true)}
                style={{ backgroundColor: '#27ae60', color: 'white', border: 'none', padding: '15px 30px', borderRadius: '12px', fontSize: '20px', cursor: 'pointer', fontWeight: 'bold', transition: '0.3s' }}
              >
                ‚ûï CADASTRAR NOVO ATLETA
              </button>
            )}
          </div>

          {mostrarForm ? (
            <FormularioAtleta aoSalvar={() => setMostrarForm(false)} aoCancelar={() => setMostrarForm(false)} />
          ) : (
            <>
              <Atletas />
              <button 
                onClick={() => window.print()} 
                style={{ marginTop: '30px', backgroundColor: '#34495e', color: 'white', border: 'none', padding: '10px 20px', borderRadius: '8px', cursor: 'pointer' }}
              >
                üñ®Ô∏è Imprimir Lista de Atletas
              </button>
            </>
          )}
        </main>
      </div>
    </div>
  );
}

export default App;
