import React, { useState, useEffect } from 'react';

const GerenciarCategorias = () => {
  const [novaCat, setNovaCat] = useState('');
  const [categoriasSalvas, setCategoriasSalvas] = useState([]);
  const [editandoIndex, setEditandoIndex] = useState(null);
  const [valorEditado, setValorEditado] = useState('');

  // Simula√ß√£o de N√≠vel de Acesso (No futuro vir√° do Login)
  const usuarioTipo = "ORGANIZADOR"; 

  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasSalvas(salvas);
  }, []);

  const salvarCategorias = (novaLista) => {
    setCategoriasSalvas(novaLista);
    localStorage.setItem('minhasCategorias', JSON.stringify(novaLista));
  };

  const criar = (e) => {
    e.preventDefault();
    if (!novaCat.trim()) return;
    salvarCategorias([...categoriasSalvas, novaCat]);
    setNovaCat('');
  };

  const excluir = (index) => {
    if (window.confirm("Aten√ß√£o √Årbitro: Deseja excluir esta categoria permanentemente?")) {
      const lista = categoriasSalvas.filter((_, i) => i !== index);
      salvarCategorias(lista);
    }
  };

  if (usuarioTipo !== "ORGANIZADOR") {
    return <div className="card-sistema"><h3>‚ö†Ô∏è Acesso restrito a Organizadores e √Årbitros.</h3></div>;
  }

  return (
    <div className="card-sistema">
      <h2>üìÅ Gerenciar Categorias (Acesso Master)</h2>
      
      <form onSubmit={criar} style={{ display: 'flex', gap: '10px', marginBottom: '30px' }}>
        <input 
          type="text" value={novaCat} onChange={(e) => setNovaCat(e.target.value)}
          placeholder="Ex: Categoria Solid√°ria / Open Arena"
          style={{ flex: 1, padding: '15px', borderRadius: '10px', border: '2px solid #1a2a6c' }}
        />
        <button type="submit" className="btn-sucesso">CRIAR CATEGORIA</button>
      </form>

      <h3>Suas Cria√ß√µes:</h3>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px' }}>
        {categoriasSalvas.map((cat, i) => (
          <div key={i} style={{ background: '#f8f9fa', padding: '15px', borderRadius: '12px', border: '1px solid #ddd', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            {editandoIndex === i ? (
              <input value={valorEditado} onChange={(e) => setValorEditado(e.target.value)} style={{ padding: '5px' }} />
            ) : (
              <span style={{ fontWeight: 'bold', color: '#f39c12' }}>{cat}</span>
            )}
            
            <div style={{ display: 'flex', gap: '5px' }}>
              {editandoIndex === i ? (
                <button onClick={() => { 
                  const n = [...categoriasSalvas]; n[i] = valorEditado; 
                  salvarCategorias(n); setEditandoIndex(null); 
                }} style={{ background: '#27ae60', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '5px' }}>OK</button>
              ) : (
                <button onClick={() => { setEditandoIndex(i); setValorEditado(cat); }} style={{ background: '#3498db', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '5px' }}>‚úèÔ∏è</button>
              )}
              <button onClick={() => excluir(i)} style={{ background: '#e74c3c', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '5px' }}>üóëÔ∏è</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GerenciarCategorias;
