import React, { useState, useEffect } from 'react';
import './index.css';
import Atletas from './components/Atletas';
import FormularioAtleta from './components/FormularioAtleta';
import GerenciarCategorias from './components/GerenciarCategorias';

function App() {
  const [tela, setTela] = useState('atletas'); // 'atletas' ou 'categorias'
  const [mostrarFormAtleta, setMostrarFormAtleta] = useState(false);

  return (
    <div className="app-container">
      <nav className="no-print" style={{ backgroundColor: '#1a2a6c', color: 'white', padding: '15px 30px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ margin: 0, fontSize: '1.8rem' }}>üèñÔ∏è Beach Master Pro</h1>
        <div style={{ background: '#f39c12', padding: '5px 15px', borderRadius: '20px', fontWeight: 'bold' }}>ADMIN</div>
      </nav>

      <div style={{ display: 'grid', gridTemplateColumns: '250px 1fr', flex: 1 }}>
        <aside style={{ background: '#fff', borderRight: '1px solid #ddd', padding: '20px' }}>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            <li onClick={() => setTela('atletas')} style={{ padding: '15px', cursor: 'pointer', fontWeight: tela === 'atletas' ? 'bold' : 'normal', fontSize: '20px' }}>üë• Atletas</li>
            <li onClick={() => setTela('categorias')} style={{ padding: '15px', cursor: 'pointer', fontWeight: tela === 'categorias' ? 'bold' : 'normal', fontSize: '20px' }}>üìÅ Categorias</li>
          </ul>
        </aside>

        <main className="content-area">
          {tela === 'atletas' ? (
            <div className="card-sistema">
              {!mostrarFormAtleta ? (
                <>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
                    <h2>Gest√£o de Atletas</h2>
                    <button onClick={() => setMostrarFormAtleta(true)} className="btn-sucesso">‚ûï NOVO ATLETA</button>
                  </div>
                  <Atletas />
                </>
              ) : (
                <FormularioAtleta aoSalvar={() => setMostrarFormAtleta(false)} aoCancelar={() => setMostrarFormAtleta(false)} />
              )}
            </div>
          ) : (
            <GerenciarCategorias />
          )}
        </main>
      </div>
    </div>
  );
}

export default App;
