import React, { useState, useEffect } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [nome, setNome] = useState('');
  const [categoriaFinal, setCategoriaFinal] = useState('');
  const [categoriasExtras, setCategoriasExtras] = useState([]);

  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasExtras(salvas);
  }, []);

  return (
    <div className="form-card">
      <h2 style={{ color: 'var(--primary-navy)', marginBottom: '25px' }}>üìù Cadastro de Atleta</h2>
      
      <form onSubmit={(e) => { e.preventDefault(); aoSalvar(); }}>
        <div style={{ marginBottom: '20px' }}>
          <label style={{ fontWeight: 'bold' }}>Nome Completo do Atleta:</label>
          <input 
            type="text" 
            className="input-master" 
            placeholder="Ex: Jo√£o Silva" 
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required 
          />
        </div>

        <div style={{ marginBottom: '30px' }}>
          <label style={{ fontWeight: 'bold' }}>Categoria Oficial:</label>
          <select 
            className="input-master" 
            value={categoriaFinal}
            onChange={(e) => setCategoriaFinal(e.target.value)}
            required
          >
            <option value="">-- Selecione o N√≠vel e Modalidade --</option>
            
            {/* CATEGORIAS PROFISSIONAIS */}
            <optgroup label="‚≠ê ELITE / PROFISSIONAL">
              <option value="PRO Masculino">PRO Masculino</option>
              <option value="PRO Feminino">PRO Feminino</option>
              <option value="PRO Misto">PRO Misto</option>
            </optgroup>

            {/* N√çVEL T√âCNICO MASCULINO */}
            <optgroup label="‚ôÇÔ∏è MASCULINO (Amador)">
              <option value="Masculino A (Avan√ßado)">Masculino A (Avan√ßado)</option>
              <option value="Masculino B (Intermedi√°rio-Alto)">Masculino B (Intermedi√°rio-Alto)</option>
              <option value="Masculino C (Intermedi√°rio-Baixo)">Masculino C (Intermedi√°rio-Baixo)</option>
              <option value="Masculino D (Iniciante)">Masculino D (Iniciante)</option>
              <option value="Masculino E (Iniciante-Base)">Masculino E (Iniciante-Base)</option>
            </optgroup>

            {/* N√çVEL T√âCNICO FEMININO */}
            <optgroup label="‚ôÄÔ∏è FEMININO (Amador)">
              <option value="Feminino A (Avan√ßado)">Feminino A (Avan√ßado)</option>
              <option value="Feminino B (Intermedi√°rio-Alto)">Feminino B (Intermedi√°rio-Alto)</option>
              <option value="Feminino C (Intermedi√°rio-Baixo)">Feminino C (Intermedi√°rio-Baixo)</option>
              <option value="Feminino D (Iniciante)">Feminino D (Iniciante)</option>
              <option value="Feminino E (Iniciante-Base)">Feminino E (Iniciante-Base)</option>
            </optgroup>

            {/* N√çVEL T√âCNICO MISTO */}
            <optgroup label="üîÑ MISTO (Duplas MX)">
              <option value="Misto A">Misto A</option>
              <option value="Misto B">Misto B</option>
              <option value="Misto C">Misto C</option>
              <option value="Misto D">Misto D</option>
              <option value="Misto E">Misto E</option>
            </optgroup>

            {/* FAIXAS ET√ÅRIAS */}
            <optgroup label="üéÇ POR IDADE (Juvenil / Masters)">
              <option value="Sub-14 (Juvenil)">Sub-14 (Juvenil)</option>
              <option value="Sub-18 (Juvenil)">Sub-18 (Juvenil)</option>
              <option value="Masters +40">Masters +40</option>
              <option value="Masters +50">Masters +50</option>
              <option value="Masters +60">Masters +60</option>
            </optgroup>

            {/* CATEGORIAS QUE VOC√ä CRIOU */}
            {categoriasExtras.length > 0 && (
              <optgroup label="‚öôÔ∏è CATEGORIAS PERSONALIZADAS">
                {categoriasExtras.map((cat, i) => (
                  <option key={i} value={cat}>{cat}</option>
                ))}
              </optgroup>
            )}
          </select>
        </div>

        <div style={{ display: 'flex', gap: '15px' }}>
          <button type="submit" className="btn-sucesso" style={{ flex: 1, fontSize: '20px' }}>SALVAR ATLETA</button>
          <button type="button" onClick={aoCancelar} style={{ flex: 1, padding: '12px', background: '#cbd5e1', border: 'none', borderRadius: '10px', fontSize: '20px', cursor: 'pointer' }}>CANCELAR</button>
        </div>
      </form>
    </div>
  );
};

export default FormularioAtleta;
