import React, { useState, useEffect } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [nome, setNome] = useState('');
  const [categoriaSelecionada, setCategoriaSelecionada] = useState('');
  
  // 1. Criamos um estado para as categorias personalizadas
  const [categoriasPersonalizadas, setCategoriasPersonalizadas] = useState([]);

  // 2. BUSCA AS CATEGORIAS QUE VOCÃŠ CRIOU (O "Pulo do Gato")
  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasPersonalizadas(salvas);
  }, []);

  const finalizarCadastro = (e) => {
    e.preventDefault();
    alert(`âœ… Atleta Cadastrado!\nNome: ${nome}\nCategoria: ${categoriaSelecionada}`);
    aoSalvar();
  };

  return (
    <div className="form-card">
      <h2 style={{ fontSize: '32px', textAlign: 'center', marginBottom: '30px' }}>ðŸŽ¾ Cadastro de Atleta</h2>
      
      <form onSubmit={finalizarCadastro}>
        <div style={{ marginBottom: '25px' }}>
          <label style={{ fontWeight: 'bold', display: 'block', fontSize: '22px' }}>Nome do Atleta:</label>
          <input 
            type="text" 
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            className="input-master"
            style={{ width: '100%', padding: '18px', fontSize: '20px', borderRadius: '12px', border: '1px solid #ccc' }}
            required
          />
        </div>

        <div style={{ marginBottom: '25px' }}>
          <label style={{ fontWeight: 'bold', display: 'block', fontSize: '22px' }}>Selecione a Categoria:</label>
          <select 
            value={categoriaSelecionada}
            onChange={(e) => setCategoriaSelecionada(e.target.value)}
            style={{ width: '100%', padding: '18px', fontSize: '20px', borderRadius: '12px', border: '2px solid #1a2a6c', backgroundColor: '#fff' }}
            required
          >
            <option value="">Selecione...</option>
            
            {/* GRUPO DAS CATEGORIAS QUE VOCÃŠ CRIOU MANUALMENTE */}
            {categoriasPersonalizadas.length > 0 && (
              <optgroup label="â­ MINHAS CATEGORIAS CRIADAS">
                {categoriasPersonalizadas.map((cat, i) => (
                  <option key={i} value={cat}>{cat}</option>
                ))}
              </optgroup>
            )}

            {/* CATEGORIAS PADRÃƒO DO SISTEMA */}
            <optgroup label="â™‚ï¸ MASCULINO PADRÃƒO">
              <option value="Masc A">Masculino A</option>
              <option value="Masc B">Masculino B</option>
            </optgroup>
            <optgroup label="â™€ï¸ FEMININO PADRÃƒO">
              <option value="Fem A">Feminino A</option>
              <option value="Fem B">Feminino B</option>
            </optgroup>
          </select>
        </div>

        <div style={{ display: 'flex', gap: '20px' }}>
          <button type="submit" className="btn-sucesso" style={{ flex: 1, fontSize: '22px' }}>CONFIRMAR</button>
          <button type="button" onClick={aoCancelar} style={{ flex: 1, padding: '20px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '15px', fontWeight: 'bold', fontSize: '22px' }}>CANCELAR</button>
        </div>
      </form>
    </div>
  );
};

export default FormularioAtleta;
