import React, { useState } from 'react';
import './index.css';
import Atletas from './components/Atletas';
import FormularioAtleta from './components/FormularioAtleta';

function App() {
  const [mostrarForm, setMostrarForm] = useState(false);

  return (
    <div className="container-mestre">
      <nav style={{ backgroundColor: '#1a2a6c', color: 'white', padding: '1rem 2rem', display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ margin: 0, fontSize: '1.8rem' }}>üèñÔ∏è Beach Master Pro</h1>
        <span style={{ background: '#f39c12', padding: '5px 15px', borderRadius: '20px', fontSize: '0.9rem' }}>ADMIN</span>
      </nav>
      
      <div className="layout-grid" style={{ display: 'grid', gridTemplateColumns: '220px 1fr', gap: '20px', padding: '20px', flex: 1 }}>
        <aside style={{ backgroundColor: 'white', padding: '15px', borderRadius: '12px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' }}>
          <ul style={{ listStyle: 'none', padding: 0, fontSize: '1.2rem' }}>
            <li style={{ padding: '12px', cursor: 'pointer', color: '#1a2a6c', fontWeight: 'bold' }}>üìä Dashboard</li>
            <li style={{ padding: '12px', cursor: 'pointer' }}>üë• Atletas</li>
            <li style={{ padding: '12px', cursor: 'pointer' }}>üèÜ Torneios</li>
          </ul>
        </aside>

        <main style={{ backgroundColor: 'white', padding: '20px', borderRadius: '12px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', overflow: 'hidden' }}>
          <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', gap: '15px', marginBottom: '20px' }}>
            <h2 style={{ margin: 0 }}>Gest√£o de Atletas</h2>
            <button 
              onClick={() => setMostrarForm(true)}
              style={{ backgroundColor: '#27ae60', color: 'white', border: 'none', padding: '12px 24px', borderRadius: '10px', fontSize: '1rem', cursor: 'pointer', fontWeight: 'bold' }}
            >
              ‚ûï NOVO ATLETA
            </button>
          </div>

          {mostrarForm ? (
            <FormularioAtleta aoSalvar={() => setMostrarForm(false)} aoCancelar={() => setMostrarForm(false)} />
          ) : (
            <div className="tabela-scroll">
              <Atletas />
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

export default App;
