import React, { useState, useEffect } from 'react';

const GerenciarCategorias = () => {
  const [novaCat, setNovaCat] = useState('');
  const [categoriasSalvas, setCategoriasSalvas] = useState([]);

  // Carrega as categorias que voc√™ j√° criou antes
  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasSalvas(salvas);
  }, []);

  const salvarCategoria = (e) => {
    e.preventDefault();
    if (!novaCat.trim()) return;

    const listaAtualizada = [...categoriasSalvas, novaCat];
    setCategoriasSalvas(listaAtualizada);
    localStorage.setItem('minhasCategorias', JSON.stringify(listaAtualizada)); // Salva no navegador!
    setNovaCat('');
    alert('Categoria "' + novaCat + '" registrada com sucesso!');
  };

  return (
    <div className="card-sistema">
      <h2>üìÅ Gerenciar Categorias</h2>
      <form onSubmit={salvarCategoria} style={{ marginBottom: '30px', display: 'flex', gap: '10px' }}>
        <input 
          type="text" 
          value={novaCat} 
          onChange={(e) => setNovaCat(e.target.value)}
          placeholder="Nome da nova categoria (Ex: Mista D)"
          style={{ flex: 1, padding: '15px', fontSize: '18px', borderRadius: '10px', border: '2px solid #1a2a6c' }}
        />
        <button type="submit" className="btn-sucesso" style={{ padding: '0 30px' }}>CRIAR AGORA</button>
      </form>

      <h3>Suas Categorias Personalizadas:</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {categoriasSalvas.length === 0 ? <p>Nenhuma categoria extra criada ainda.</p> : 
          categoriasSalvas.map((cat, i) => (
            <span key={i} className="categoria-label" style={{ background: '#f39c12', color: 'white' }}>{cat}</span>
          ))
        }
      </div>
    </div>
  );
};

export default GerenciarCategorias;
