import React, { useState } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [nome, setNome] = useState('');
  const [categoriaSelecionada, setCategoriaSelecionada] = useState('');
  const [nomeNovaCategoria, setNomeNovaCategoria] = useState('');
  
  // Lista inicial de categorias (MemÃ³ria do Sistema)
  const [listaCategorias, setListaCategorias] = useState([
    { grupo: "â™‚ï¸ MASCULINO", opcoes: ["Masculino A (PrÃ³)", "Masculino B", "Masculino C", "Masculino D", "Masculino E (Iniciante)"] },
    { grupo: "â™€ï¸ FEMININO", opcoes: ["Feminino A (PrÃ³)", "Feminino B", "Feminino C", "Feminino D", "Feminino E (Iniciante)"] },
    { grupo: "ðŸ”„ MISTA", opcoes: ["Mista A", "Mista B", "Mista C", "Mista D", "Mista E (Iniciante)"] },
    { grupo: "ðŸŽ‚ IDADE / OUTROS", opcoes: ["Sub-12", "Sub-14", "Master 40+", "Master 50+"] }
  ]);

  const finalizarCadastro = (e) => {
    e.preventDefault();
    
    let categoriaFinal = categoriaSelecionada;

    // LÃ³gica para Criar e Salvar a nova categoria de verdade
    if (categoriaSelecionada === 'OUTRA') {
      if (!nomeNovaCategoria.trim()) {
        alert("Por favor, digite o nome da nova categoria!");
        return;
      }
      categoriaFinal = nomeNovaCategoria;
      
      // Adiciona a nova categoria no grupo "OUTROS" para aparecer na prÃ³xima vez
      const novaLista = [...listaCategorias];
      novaLista[3].opcoes.push(nomeNovaCategoria);
      setListaCategorias(novaLista);
    }

    alert(`âœ… Sucesso!\nAtleta: ${nome}\nCategoria: ${categoriaFinal}\n\nO Beach Master Pro registrou o jogador!`);
    aoSalvar(); // Fecha o formulÃ¡rio
  };

  return (
    <div className="form-card">
      <h2 style={{ fontSize: '32px', textAlign: 'center', marginBottom: '30px' }}>ðŸŽ¾ Cadastro de Atleta</h2>
      
      <form onSubmit={finalizarCadastro}>
        {/* Campo Nome */}
        <div style={{ marginBottom: '25px' }}>
          <label style={{ fontWeight: 'bold', display: 'block', fontSize: '22px' }}>Nome do Atleta:</label>
          <input 
            type="text" 
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            style={{ width: '100%', padding: '18px', fontSize: '20px', borderRadius: '12px', border: '1px solid #ccc', marginTop: '10px', boxSizing: 'border-box' }}
            placeholder="Nome Completo"
            required
          />
        </div>

        {/* SeleÃ§Ã£o de Categoria */}
        <div style={{ marginBottom: '25px' }}>
          <label style={{ fontWeight: 'bold', display: 'block', fontSize: '22px' }}>Categoria (Role para ver todas):</label>
          <select 
            value={categoriaSelecionada}
            onChange={(e) => setCategoriaSelecionada(e.target.value)}
            style={{ width: '100%', padding: '18px', fontSize: '20px', borderRadius: '12px', border: '2px solid #1a2a6c', marginTop: '10px', backgroundColor: '#fff' }}
            required
          >
            <option value="">Selecione uma categoria...</option>
            {listaCategorias.map((grupo, index) => (
              <optgroup key={index} label={grupo.grupo}>
                {grupo.opcoes.map(opcao => (
                  <option key={opcao} value={opcao}>{opcao}</option>
                ))}
              </optgroup>
            ))}
            <option value="OUTRA" style={{ fontWeight: 'bold', color: 'blue' }}>âž• CRIAR NOVA CATEGORIA...</option>
          </select>
        </div>

        {/* Campo que aparece sÃ³ para criar nova categoria */}
        {categoriaSelecionada === 'OUTRA' && (
          <div style={{ marginBottom: '25px', backgroundColor: '#eef2f7', padding: '20px', borderRadius: '15px', border: '2px dashed #1a2a6c' }}>
            <label style={{ fontWeight: 'bold', display: 'block', fontSize: '20px', marginBottom: '10px' }}>Nome da Nova Categoria:</label>
            <input 
              type="text" 
              value={nomeNovaCategoria}
              onChange={(e) => setNomeNovaCategoria(e.target.value)}
              placeholder="Ex: Pais e Filhos / Iniciante D"
              style={{ width: '100%', padding: '15px', fontSize: '18px', borderRadius: '10px', border: '1px solid #1a2a6c' }}
            />
          </div>
        )}

        {/* BotÃµes Grandes */}
        <div style={{ display: 'flex', gap: '20px' }}>
          <button type="submit" style={{ flex: 1, padding: '20px', backgroundColor: '#27ae60', color: 'white', border: 'none', borderRadius: '15px', fontWeight: 'bold', fontSize: '22px', cursor: 'pointer' }}>
            CONFIRMAR
          </button>
          <button type="button" onClick={aoCancelar} style={{ flex: 1, padding: '20px', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '15px', fontWeight: 'bold', fontSize: '22px', cursor: 'pointer' }}>
            CANCELAR
          </button>
        </div>
      </form>
    </div>
  );
};

export default FormularioAtleta;
