import React, { useState, useEffect } from 'react';

const GerenciarCategorias = () => {
  const [categoriasSalvas, setCategoriasSalvas] = useState([]);
  
  useEffect(() => {
    const salvas = JSON.parse(localStorage.getItem('minhasCategorias')) || [];
    setCategoriasSalvas(salvas);
  }, []);

  return (
    <div style={{ padding: '30px', maxWidth: '1000px', margin: '0 auto' }}>
      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ color: 'var(--primary-navy)', fontSize: '28px' }}>ðŸ“‚ Gerenciamento Master</h2>
        <p style={{ color: '#64748b' }}>Administre as categorias personalizadas da sua arena.</p>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: '20px' }}>
        {categoriasSalvas.map((cat, i) => (
          <div key={i} className="categoria-card">
            <span className="categoria-nome">{cat}</span>
            
            <div style={{ display: 'flex', gap: '8px' }}>
              <button className="btn-icon edit" title="Editar">
                <svg width="18" height="18" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"></path></svg>
              </button>
              <button className="btn-icon delete" title="Excluir">
                <svg width="18" height="18" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"></path></svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GerenciarCategorias;
