import React, { useState } from 'react';

const GerenciarCategorias = () => {
  const [minhasCategorias] = useState(['Especial', 'Feminina - Especial E', 'Masculino Especial D']);

  return (
    <div className="content-wrapper">
      <h1 style={{ fontSize: '28px', marginBottom: '8px' }}>üìÇ Gest√£o de Categorias</h1>
      <p style={{ color: 'var(--gray-500)', marginBottom: '32px' }}>Personalize as modalidades exclusivas do seu evento.</p>

      <div className="main-card" style={{ marginBottom: '40px' }}>
        <label style={{ display: 'block', fontWeight: 'bold', marginBottom: '12px' }}>Criar Nova Modalidade:</label>
        <div style={{ display: 'flex', gap: '16px' }}>
          <input 
            className="input-premium" 
            placeholder="Ex: Mista Open Solid√°ria" 
            style={{ flex: 1, padding: '16px', borderRadius: '12px', border: '1px solid var(--gray-200)' }}
          />
          <button style={{ background: 'var(--beach-action)', color: 'white', border: 'none', padding: '0 32px', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer' }}>
            ADICIONAR
          </button>
        </div>
      </div>

      <h3 style={{ fontSize: '14px', textTransform: 'uppercase', color: 'var(--gray-500)', letterSpacing: '1px', marginBottom: '16px' }}>
        ‚≠ê Suas Cria√ß√µes:
      </h3>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '16px' }}>
        {minhasCategorias.map((cat, i) => (
          <div key={i} style={{ background: 'white', padding: '20px', borderRadius: '15px', border: '1px solid var(--gray-200)', display: 'flex', justifyContent: 'space-between', alignItems: 'center', boxShadow: 'var(--shadow-sm)' }}>
            <span style={{ fontWeight: '600' }}>{cat}</span>
            <div style={{ display: 'flex', gap: '8px' }}>
              <button style={{ border: 'none', background: '#f1f5f9', padding: '8px', borderRadius: '8px' }}>‚úèÔ∏è</button>
              <button style={{ border: 'none', background: '#fef2f2', padding: '8px', borderRadius: '8px' }}>üóëÔ∏è</button>
            </div>
          </div>
        ))}
      </div>

      <div style={{ marginTop: '60px', padding: '24px', background: '#f8fafc', borderRadius: '15px', border: '1px solid var(--gray-200)' }}>
        <h4 style={{ margin: '0 0 12px 0', fontSize: '13px', color: 'var(--navy-surface)' }}>üîí CATEGORIAS PADR√ÉO DO SISTEMA (IMUT√ÅVEIS)</h4>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
          {['Fem PRO', 'Masc PRO', 'Sub-14', 'Masters +40', 'Open Mista'].map(p => (
            <span key={p} style={{ background: 'white', border: '1px solid var(--gray-200)', padding: '6px 14px', borderRadius: '8px', fontSize: '12px', color: 'var(--gray-500)' }}>{p}</span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GerenciarCategorias;
