import React, { useState } from 'react';

const FormularioAtleta = ({ aoSalvar, aoCancelar }) => {
  const [nome, setNome] = useState('');
  const [categoria, setCategoria] = useState('A');

  const enviar = (e) => {
    e.preventDefault();
    alert('Atleta ' + nome + ' enviado para o banco de dados!');
    aoSalvar();
  };

  return (
    <div style={{ backgroundColor: '#fff', padding: '20px', borderRadius: '8px', border: '1px solid #ddd', marginBottom: '20px' }}>
      <h4>üìù Cadastrar Novo Jogador</h4>
      <form onSubmit={enviar}>
        <div style={{ marginBottom: '10px' }}>
          <label>Nome do Atleta: </label><br/>
          <input 
            type="text" 
            value={nome} 
            onChange={(e) => setNome(e.target.value)} 
            style={{ width: '100%', padding: '8px', borderRadius: '4px', border: '1px solid #ccc' }} 
            placeholder="Ex: Rafael Nadal"
            required
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label>Categoria: </label><br/>
          <select value={categoria} onChange={(e) => setCategoria(e.target.value)} style={{ width: '100%', padding: '8px' }}>
            <option value="A">Categoria A (Pr√≥)</option>
            <option value="B">Categoria B (Intermedi√°rio)</option>
            <option value="C">Categoria C (Iniciante)</option>
          </select>
        </div>
        <button type="submit" style={{ backgroundColor: '#28a745', color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer', marginRight: '10px' }}>
          Salvar Atleta
        </button>
        <button type="button" onClick={aoCancelar} style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '10px 15px', borderRadius: '5px', cursor: 'pointer' }}>
          Cancelar
        </button>
      </form>
    </div>
  );
};

export default FormularioAtleta;
